mapper.select:
select

from sys_log
where username=#{username,jdbcType=CHAR}

    and operaTime > #{minOperatime,jdbcType=TIMESTAMP}


    and operaTime < #{maxOperatime,jdbcType=TIMESTAMP}

order by operaTime desc

select

from sys_log


        and username=#{param1.username,jdbcType=CHAR}


        and clientIp=#{param1.clientip,jdbcType=VARCHAR}


        and operaTime > #{minOperatime,jdbcType=TIMESTAMP}


        and operaTime < #{maxOperatime,jdbcType=TIMESTAMP}


        and isSuccess=#{param1.issuccess,jdbcType=BOOLEAN}


order by operaTime desc
